CREATE TABLE ASSESSMENT_STUDENT_ANSWER
as
SELECT
    LOWER(REGEXP_REPLACE(dbms_crypto.randombytes(16), '(.{8})(.{4})(.{4})(.{4})(.{12})', '\1-\2-\3-\4-\5')) as ASSESSMENT_STUDENT_ANSWER_ID,
    (SELECT comp.ASSESSMENT_STUDENT_COMPONENT_ID FROM ASSESSMENT_STUDENT_COMPONENT comp
     WHERE TRIM(assmnt_mc.COMPONENT_TYPE) = comp.COMPONENT_SUB_TYPE_CODE
       AND TRIM(assmnt_mc.MC_OE_FLAG) = comp.COMPONENT_TYPE_CODE
       AND TRIM(assmnt_mc.ASSMT_SESSION) = comp.ASSMT_SESSION
       AND TRIM(assmnt_mc.STUD_NO) = comp.STUD_NO
       AND TRIM(assmnt_mc.ASSMT_CODE) = comp.ASSMT_CODE) AS ASSESSMENT_STUDENT_COMPONENT_ID,
    (SELECT ques.ASSESSMENT_QUESTION_ID FROM ASSESSMENT_SESSION sess, ASSESSMENT assmnt, ASSESSMENT_FORM form, ASSESSMENT_COMPONENT key_comp, ASSESSMENT_STUDENT stud, ASSESSMENT_QUESTION ques
     WHERE sess.COURSE_YEAR = SUBSTR( TRIM(assmnt_mc.ASSMT_SESSION), 0 , 4)
       AND sess.COURSE_MONTH = SUBSTR(TRIM(assmnt_mc.ASSMT_SESSION), 5)
       AND assmnt.SESSION_ID = sess.SESSION_ID
       AND form.ASSESSMENT_ID = assmnt.ASSESSMENT_ID
       AND key_comp.ASSESSMENT_FORM_ID = form.ASSESSMENT_FORM_ID
       AND ques.ASSESSMENT_COMPONENT_ID = key_comp.ASSESSMENT_COMPONENT_ID
       AND ques.QUESTION_NUMBER = assmnt_mc.QUES_NUMBER
       AND ques.ITEM_NUMBER = assmnt_mc.ITEM_NUMBER
       AND stud.ASSESSMENT_ID = assmnt.ASSESSMENT_ID
       AND stud.ASSESSMENT_FORM_ID = form.ASSESSMENT_FORM_ID
       AND TRIM(assmnt_mc.STUD_NO) = stud.PEN
       AND TRIM(assmnt_mc.COMPONENT_TYPE) = key_comp.COMPONENT_SUB_TYPE_CODE
       AND TRIM(assmnt_mc.MC_OE_FLAG) = key_comp.COMPONENT_TYPE_CODE
       AND TRIM(assmnt_mc.ASSMT_CODE) = assmnt.ASSESSMENT_TYPE_CODE) AS ASSESSMENT_QUESTION_ID,
    assmnt_mc.ITEM_NUMBER as ITEM_NUMBER,
    assmnt_mc.QUES_NUMBER as QUESTION_NUMBER,
    TRIM(assmnt_mc.ASSMT_CODE) as ASSMT_CODE,
    TRIM(assmnt_mc.COMPONENT_TYPE) as COMPONENT_TYPE,
    TRIM(assmnt_mc.MC_OE_FLAG) as MC_OE_FLAG,
    TRIM(assmnt_mc.ASSMT_SESSION) as ASSMT_SESSION,
    TRIM(assmnt_mc.STUD_NO) as STUD_NO,
    assmnt_mc.SCORE as SCORE,
    'ASSESSMENT-API' as CREATE_USER,
    SYSDATE as CREATE_DATE,
    'ASSESSMENT-API' as UPDATE_USER,
    SYSDATE as UPDATE_DATE
FROM STUD_GRAD_ASSMT_MCOE_MR assmnt_mc;

DELETE FROM ASSESSMENT_STUDENT_ANSWER asa WHERE score = 0 AND question_number = 0;
DELETE FROM ASSESSMENT_STUDENT_ANSWER asa WHERE score = -1;
DELETE FROM ASSESSMENT_STUDENT_ANSWER asa WHERE ASSESSMENT_STUDENT_COMPONENT_ID IS NULL;

CREATE TABLE ASSESSMENT_STUDENT_CHOICE
as
SELECT
    LOWER(REGEXP_REPLACE(dbms_crypto.randombytes(16), '(.{8})(.{4})(.{4})(.{4})(.{12})', '\1-\2-\3-\4-\5')) as ASSESSMENT_STUDENT_CHOICE_ID,
    (SELECT comp.ASSESSMENT_STUDENT_COMPONENT_ID FROM ASSESSMENT_STUDENT_COMPONENT comp
     WHERE TRIM(assmnt_mc.COMPONENT_TYPE) = comp.COMPONENT_SUB_TYPE_CODE
       AND TRIM(assmnt_mc.MC_OE_FLAG) = comp.COMPONENT_TYPE_CODE
       AND TRIM(assmnt_mc.ASSMT_SESSION) = comp.ASSMT_SESSION
       AND TRIM(assmnt_mc.STUD_NO) = comp.STUD_NO
       AND TRIM(assmnt_mc.ASSMT_CODE) = comp.ASSMT_CODE) AS ASSESSMENT_STUDENT_COMPONENT_ID,
    (SELECT choice.ASSESSMENT_CHOICE_ID FROM ASSESSMENT_STUDENT_COMPONENT comp, ASSESSMENT_CHOICE choice
     WHERE TRIM(assmnt_mc.COMPONENT_TYPE) = comp.COMPONENT_SUB_TYPE_CODE
       AND TRIM(assmnt_mc.MC_OE_FLAG) = comp.COMPONENT_TYPE_CODE
       AND TRIM(assmnt_mc.ASSMT_SESSION) = comp.ASSMT_SESSION
       AND TRIM(assmnt_mc.STUD_NO) = comp.STUD_NO
       AND TRIM(assmnt_mc.ASSMT_CODE) = comp.ASSMT_CODE
       AND choice.ASSESSMENT_COMPONENT_ID = comp.ASSESSMENT_COMPONENT_ID
       AND TRIM(assmnt_mc.ITEM_NUMBER) = choice.ITEM_NUMBER) AS ASSESSMENT_CHOICE_ID,
    TRIM(assmnt_mc.ASSMT_CODE) as ASSMT_CODE,
    TRIM(assmnt_mc.ASSMT_SESSION) as ASSMT_SESSION,
    TRIM(assmnt_mc.MC_OE_FLAG) as COMPONENT_TYPE_CODE,
    TRIM(assmnt_mc.STUD_NO) as STUD_NO,
    assmnt_mc.ITEM_NUMBER as ITEM_NUMBER,
    assmnt_mc.QUESTION_NUMBER as QUESTION_NUMBER,
    assmnt_mc.SCORE as SCORE,
    TRIM(assmnt_mc.COMPONENT_TYPE) as COMPONENT_SUB_TYPE_CODE,
    'ASSESSMENT-API' as CREATE_USER,
    SYSDATE as CREATE_DATE,
    'ASSESSMENT-API' as UPDATE_USER,
    SYSDATE as UPDATE_DATE
FROM ASSESSMENT_STUDENT_ANSWER assmnt_mc
where assmnt_mc.ASSESSMENT_QUESTION_ID is null;

