UPDATE
    ASSESSMENT_QUESTION AQ
SET
    SCALE_FACTOR = (
        SELECT
            SCALE_FACTOR
        FROM
            ASSESSMENT_QUESTION Q1
        WHERE
            Q1.ASSESSMENT_COMPONENT_ID = AQ.ASSESSMENT_COMPONENT_ID
          AND Q1.ITEM_NUMBER = AQ.ITEM_NUMBER
          AND SCALE_FACTOR IS NOT NULL
          AND ROWNUM < 2)
WHERE
    AQ.SCALE_FACTOR IS NULL;

